function snxTimer(e){this.settings=$.extend({interval:1,wait:0,onTime:function(){}},e);this.timer=null;this.timeInit=null;return this}snxTimer.prototype={run:function(){var e=this;this.timeInit+=this.settings.interval*1e3;this.settings.onTime();this.timer=setTimeout(function(){e.run()},this.timeInit-(new Date).getTime())},start:function(){if(this.timer==null)if(this.settings.wait>0){var e=this;this.timer=setTimeout(function(){e.timer=null;e.settings.wait=0;e.start()},this.settings.wait*1e3)}else{this.timeInit=(new Date).getTime();this.run()}},stop:function(){clearTimeout(this.timer);this.timer=null},pause:function(e){e>0&&(this.settings.wait=e);this.stop()}};